mapper.select:
SELECT * FROM agency
deleted = 0
AND login_name = #{loginKey}
AND password = #{loginPwd}

with temp( id, agency_type, parent_id, parent_nickname, nickname, avatar, phone, room_card, coin, agency_balance, [disable], deleted, create_date, creator_id, user_id, level ) AS ( SELECT id, agency_type, parent_id, parent_nickname, nickname, avatar, phone, room_card, coin, agency_balance, [disable], deleted, create_date, creator_id, user_id, 0 AS level FROM agency WHERE id = #{agencyId} UNION ALL SELECT a.id, a.agency_type, a.parent_id, a.parent_nickname, a.nickname, a.avatar, a.phone, a.room_card, a.coin, a.agency_balance, a.[disable], a.deleted, a.create_date, a.creator_id, a.user_id, temp.level + 1 FROM agency a INNER JOIN temp on a.parent_id = temp.id ) SELECT * FROM temp
level <= #{level}
AND id = #{params.agencyId}
AND nickname like '%' + #{params.nickname} + '%'
AND parent_id = #{params.parentId}

mapper.update:
UPDATE
    agency
SET
    coin -= #{quantity}
WHERE
    id = #{agencyId}

UPDATE
    agency
SET
    room_card -= #{quantity}
WHERE
id = #{agencyId}
