mapper.select:
SELECT e.id AS id, e.agency_id AS agencyId, e.enchashment_type AS enchashmentType, e.money AS money, e.weixin AS weixin, e.alipay AS alipay, e.bankno AS bankno, e.approve_status, e.create_datetime, e.approve_datetime, a.nickname AS agencyNickname, a.agency_type AS agencyType, a.avatar AS avatar FROM enchashment e INNER JOIN agency a ON e.agency_id = a.id
a.id = #{agencyId}
and a.nickname like '%' + #{agencyNickname} + '%'
